(542, 1, '2021-12-29 09:21:00', '2021-12-29 09:21:00', '{\n    \"big-store::big_store_top_slide_content\": {\n        \"value\": \"[{\\\"text\\\":\\\"Click Here\\\",\\\"link\\\":\\\"\\\",\\\"text2\\\":\\\"undefined\\\",\\\"image_url\\\":\\\"http://localhost/jutta/wp-content/uploads/2021/12/carousel.jpg\\\",\\\"title\\\":\\\"\\\",\\\"price\\\":\\\"undefined\\\",\\\"number\\\":\\\"undefined\\\",\\\"subtitle\\\":\\\"All progress takes place outside the comfort zone\\\",\\\"social_repeater\\\":\\\"undefined\\\",\\\"id\\\":\\\"social-repeater-5ff6f10128c33\\\",\\\"shortcode\\\":\\\"undefined\\\"}]\",\n        \"type\": \"theme_mod\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2021-12-29 09:20:05\"\n    },\n    \"big-store::big_store_top_slide_layout\": {\n        \"value\": \"slide-layout-1\",\n        \"type\": \"theme_mod\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2021-12-29 09:19:05\"\n    },\n    \"big-store::big_store_ribbon_bg_img_url\": {\n        \"value\": \"http://localhost/jutta/wp-content/uploads/2021/12/martin-katler-Y4fKN-RlMV4-unsplash-scaled.jpg\",\n        \"type\": \"theme_mod\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2021-12-29 09:20:30\"\n    },\n    \"big-store::big_store_ribbon_bg_img_id\": {\n        \"value\": 541,\n        \"type\": \"theme_mod\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2021-12-29 09:20:30\"\n    }\n}', '', '', 'trash', 'closed', 'closed', '', '145c41da-5183-4e6c-9d83-e8cedaf3c001', '', '', '2021-12-29 09:21:00', '2021-12-29 09:21:00', '', 0, 'http://localhost/jutta/?p=542', 0, 'customize_changeset', '', 0),
